    private void createCardView(int carId, String carAlias, String carModelsAlias, int carMinprice, String carModelsBodiesPhoto, String carModelsBodiesAlias, String carCountryCode) {
        car = new Car(carId, carAlias, carModelsAlias, carMinprice);
        car.setCarPhotoImageView(carModelsBodiesPhoto);
        car.setCarModelsBodiesAlias(carModelsBodiesAlias);
        car.setCarCountryCode(carCountryCode);
        LinearLayout child = new LinearLayout(context);
        child.setOrientation(LinearLayout.HORIZONTAL);
        child.setBackgroundColor(Color.rgb(51, 51, 153));

        LinearLayout.LayoutParams layoutParams = new LinearLayout.LayoutParams(
                LinearLayout.LayoutParams.MATCH_PARENT, LinearLayout.LayoutParams.WRAP_CONTENT);

        layoutParams.setMargins(20, 20, 20, 10);

//        child.setLayoutParams(new LinearLayout.LayoutParams(LinearLayout.LayoutParams.MATCH_PARENT, LinearLayout.LayoutParams.WRAP_CONTENT));
//        child.set

        View cardView = LayoutInflater.from(context).inflate(R.layout.card_view, null);

        String carPhotoUrl = car.getCarPhotoImageView();
        ImageView carPhotoImageView = cardView.findViewById(R.id.carPhotoImageView);
        Picasso.with(AvitoActivity.this).load(carPhotoUrl).into(carPhotoImageView);

        TextView headerTextView = cardView.findViewById(R.id.headerTextView);
        TextView priceTextView = cardView.findViewById(R.id.priceTextView);
        TextView descriptionTextView = cardView.findViewById(R.id.descriptionTextView);
        String header = car.getCarAlias() + " " + car.getCarModelsBodiesAlias() + " " + car.getCarModelAlias() + " (" + car.getCarCountryCode() + ")";
        String price = car.getCarMinprice() + " Рублей";
        headerTextView.setText(header);
        priceTextView.setText(price);

        child.addView(cardView, layoutParams);

        cardView.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent intent = new Intent(AvitoActivity.this, CarCalculatorActivity.class);
                intent.putExtra("car_id", car.getCarId());
                intent.putExtra("car_photo_url", car.getCarPhotoImageView());
                intent.putExtra("car_alias", car.getCarAlias());
                intent.putExtra("car_models_bodies_alias", car.getCarModelsBodiesAlias());
                intent.putExtra("car_models_alias", car.getCarModelAlias());
                intent.putExtra("car_country_code", car.getCarCountryCode());
                intent.putExtra("car_minprice", car.getCarMinprice());
                startActivity(intent);
            }
        });

        activityAvitoCarListLayout.addView(child);
    }

